import React, { Component } from 'react';
import List from './List';
import request from 'superagent';

	export default class Search extends Component {

        state = {
            beers: [],
            favorites: [],
            input: ''
        }

        componentDidMount = async () => {
            const favorites = await request.get('http://damp-meadow-28672.herokuapp.com/api/my/favorites')
                .set('Authorization', this.props.user.token);

            this.setState({ favorites: favorites.body });
        }

        handleInput = (event) => {
            this.setState({ input: event.target.value })
        }

        handleClick = async (event) => {
            event.preventDefault();
            const data = await request.get(`http://localhost:5000/api/beers?search=${this.state.input}`);
            // const beers = data.body.data;
            const beers = data.body.data;

            this.setState({ beers })
            console.log(beers);
        }

		render() {
			return (
                <div>
                    <div className="search-container">
                        <form>
                            <input 
                                value={this.state.input} 
                                onChange={this.handleInput}  
                            />
                            <button onClick={this.handleClick}>Search</button>
                        </form>
                    </div>
                    <div className="list-container">
                        <List beers={this.state.beers} />
                    </div>
                </div>
			)
		}
	}
