import React, { Component } from 'react';
import request from 'superagent';
import { withRouter } from 'react-router-dom';
import Item from './Item';

	export default withRouter(class App extends Component {
        makeFavorite = async (beer) => {
            const favorite = await request.post('damp-meadow-28672.herokuapp.com/api/my/favorites', {
                name: beer.name,
                ibu: beer.ibu,
                abv: beer.abv
            })
            .set('Authorization', this.props.user.token)
        }

        renderButtonOrStar = (beer) => {
            const isOnFavoritesList = this.props.favorites.find(favBeer => beer.name === favBeer.name);
            if(!isOnFavoritesList) {
                return <button onClick={ (event) => this.makeFavorite(beer) }>Favorite</button>
            }
            return <span>‚≠ê</span>
        }
		render() {
			return (
				<ul>
                    {this.props.beers.map(beer => 
                        <Item beer={beer} key={beer.id} />
                    )}
                </ul>
			)
		}
    })
