import React, { Component } from 'react';
import request from 'superagent';

	export default class App extends Component {
        state = {
            usernameSignUp: '',
            passwordSignUp: '',
            usernameSignIn: '',
            passwordSignIn: ''
        }

        handleSignUp = async () => {
            const signUp = await request.post(`http://damp-meadow-28672.herokuapp.com/api/auth/signup`, {
                email: this.state.usernameSignUp,
                password: this.state.passwordSignUp,
            })

            localStorage.setItem('user', JSON.stringify(signUp.body));
        }

        handleSignIn = async () => {
            const signIn = await request.post(`http://damp-meadow-28672.herokuapp.com/api/auth/signin`, {
                email: this.state.usernameSignIn,
                password: this.state.passwordSignIn,
            })

            localStorage.setItem('user', JSON.stringify(signIn.body));

            window.location = '/';
        }

		render() {
			return (
                <div>
                    <div className="signup-container">
                        <label  htmlFor="email-signup">Email: </label>
                        <input 
                            name="email-signup" 
                            value={this.state.usernameSignUp} 
                            onChange={(e) => this.setState({ usernameSignUp: e.target.value})} 
                        />
                        <label htmlFor="password-signup">Password: </label>
                        <input 
                            name="password-signup" 
                            value={this.state.passwordSignUp} 
                            onChange={(e) => this.setState({ passwordSignUp: e.target.value})} 
                        />
                        <button onClick={this.handleSignUp}>
                            Sign Up
                        </button>
                    </div>
                    <div className="signin-container">
                        <label>Email: </label>
                        <input 
                            value={this.state.usernameSignIn} 
                            onChange={(e) => this.setState({ usernameSignIn: e.target.value})} 
                        />
                        <label>Password: </label>
                        <input 
                            value={this.state.passwordSignIn} 
                            onChange={(e) => this.setState({ passwordSignIn: e.target.value})} 
                        />
                        <button onClick={this.handleSignIn}>
                            Sign In
                        </button>
                    </div>
                </div>
			)
		}
	}
